"use client"

import { useState, useRef, useEffect } from "react"
import "../../styles/header.scss"

export default function Header() {
  const [showCatalog, setShowCatalog] = useState(false)
  const dropdownRef = useRef(null)

  const catalogItems = [
    "Electronics",
    "Fashion",
    "Home & Garden",
    "Sports",
    "Books",
  ]

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setShowCatalog(false)
      }
    }
    document.addEventListener("mousedown", handleClickOutside)
    return () => document.removeEventListener("mousedown", handleClickOutside)
  }, [])

  return (
    <div className="header-desktop">
      <div className="header-title">Trustee</div>

      <div className="header-search">
        <div className="dropdown-container" ref={dropdownRef}>
          <div
            className="dropdown-header"
            onClick={() => setShowCatalog((prev) => !prev)}
          >
            <span>Catalog</span>
            <span style={{ transform: showCatalog ? "rotate(180deg)" : "rotate(0deg)" }}>â–¼</span>
          </div>

          {showCatalog && (
            <div className="dropdown-menu">
              {catalogItems.map((item, idx) => (
                <div
                  key={idx}
                  className="dropdown-item"
                  onClick={() => {
                    console.log("Selected:", item)
                    setShowCatalog(false)
                  }}
                >
                  {item}
                </div>
              ))}
            </div>
          )}
        </div>

        <input className="search-input" placeholder="Search products..." />
      </div>

      <div className="header-actions">
        <button className="action-btn">User</button>
        <button className="action-btn">Cart</button>
        <button className="action-btn">Sell Now</button>
      </div>
    </div>
  )
}
