"use client"

import { useState, useEffect, useRef } from "react"
import { motion, AnimatePresence } from "framer-motion"
import { Menu, Search, User, ShoppingCart, ChevronDown, X, Sparkles, Store, HelpCircle } from "lucide-react"
import "../../styles/landingpage/header.scss"

export default function Header({ viewport }) {
  // STATES
  const [showCatalog, setShowCatalog] = useState(true)
  const [showMenu, setShowMenu] = useState(false)
  const [scrolled, setScrolled] = useState(false)
  const [searchFocused, setSearchFocused] = useState(false)
  const [dropdownPosition, setDropdownPosition] = useState({ top: 0, left: 0 })
  const dropdownRef = useRef(null)

  // CATALOG ITEMS
  const catalogItems = [
    { name: "Electronics", icon: "ðŸ’»" },
    { name: "Fashion", icon: "ðŸ‘•" },
    { name: "Home & Garden", icon: "ðŸ¡" },
    { name: "Sports", icon: "âš½" },
    { name: "Books", icon: "ðŸ“š" },
  ]

  const mobileMenuItems = [
    { name: "Catalog", Icon: ChevronDown },
    { name: "Sellers", Icon: Store },
    { name: "Help Center", Icon: HelpCircle },
    { name: "Sign in / Sign up", Icon: User },
    { name: "Sell Now", Icon: Sparkles, highlight: true },
  ]

  // SCROLL DETECTION
  useEffect(() => {
    const handleScroll = () => setScrolled(window.scrollY > 20)
    window.addEventListener("scroll", handleScroll)
    return () => window.removeEventListener("scroll", handleScroll)
  }, [])

  // CLOSE DROPDOWN ON OUTSIDE CLICK
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        dropdownRef.current &&
        !dropdownRef.current.contains(event.target)
      ) {
        setShowCatalog(false)
      }
    }
    document.addEventListener("mousedown", handleClickOutside)
    return () => document.removeEventListener("mousedown", handleClickOutside)
  }, [])

  // TOGGLE CATALOG (Desktop)
  const toggleCatalog = () => {
    if (dropdownRef.current) {
      const rect = dropdownRef.current.getBoundingClientRect()
      setDropdownPosition({ top: rect.bottom + 8, left: rect.left })
    }
    setShowCatalog((prev) => !prev)
  }

  const toggleMenu = () => setShowMenu((prev) => !prev)
  const closeMenu = () => setShowMenu(false)

  // =========================
  // MOBILE HEADER
  // =========================
  if (viewport === "xs") {
    return (
      <motion.div
        className={`header-mobile ${scrolled ? "scrolled" : ""}`}
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ type: "spring", stiffness: 100, damping: 20 }}
      >
        <div className="header-mobile-content">
          <motion.h3
            className="header-title"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            whileHover={{ scale: 1.05 }}
          >
            <Sparkles size={20} className="title-sparkle" />
            Trustee
          </motion.h3>

          <motion.div
            whileHover={{ scale: 1.1, rotate: 90 }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            <Menu size={28} className="menu-icon" onClick={toggleMenu} />
          </motion.div>
        </div>

        {/* Mobile Menu Overlay */}
        <AnimatePresence>
          {showMenu && (
            <>
              <motion.div
                className="menu-overlay"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                onClick={closeMenu}
              />
              <motion.div
                className="header-hamburger-dropdown"
                initial={{ x: "100%" }}
                animate={{ x: 0 }}
                exit={{ x: "100%" }}
                transition={{ type: "spring", stiffness: 100, damping: 20 }}
              >
                <div className="menu-header">
                  <motion.h4
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.2 }}
                  >
                    Menu
                  </motion.h4>
                  <motion.button
                    className="close-btn"
                    onClick={closeMenu}
                    whileHover={{ scale: 1.1, rotate: 90 }}
                    whileTap={{ scale: 0.9 }}
                  >
                    <X size={24} />
                  </motion.button>
                </div>

                <div className="menu-items">
                  {mobileMenuItems.map((item, idx) => {
                    const IconComponent = item.Icon
                    return (
                      <motion.div
                        key={item.name}
                        className={`menu-item ${item.highlight ? "highlight" : ""}`}
                        initial={{ x: 50, opacity: 0 }}
                        animate={{ x: 0, opacity: 1 }}
                        transition={{ delay: idx * 0.1 }}
                        whileHover={{ x: 10 }}
                      >
                        <IconComponent size={20} className="menu-item-icon" />
                        <span>{item.name}</span>
                      </motion.div>
                    )
                  })}
                </div>
              </motion.div>
            </>
          )}
        </AnimatePresence>
      </motion.div>
    )
  }

  // =========================
  // DESKTOP HEADER
  // =========================
  return (
    <>
      <motion.div
        className={`header-desktop ${scrolled ? "scrolled" : ""}`}
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ type: "spring", stiffness: 100, damping: 20 }}
      >
        <div className="header-content">
          {/* Business Name */}
          <motion.div
            className="header-business"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
          >
            <motion.h3
              className="header-title"
              whileHover={{ scale: 1.05 }}
              transition={{ type: "spring", stiffness: 400 }}
            >
              <motion.span
                animate={{ rotate: [0, 360] }}
                transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
                style={{ display: "inline-block" }}
              >
                <Sparkles size={24} className="title-sparkle" />
              </motion.span>
              Trustee
            </motion.h3>
          </motion.div>

          {/* Search Area */}
          <motion.div
            className="header-search"
            ref={dropdownRef}
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <div className="search-wrapper">
              {/* Catalog Dropdown */}
              <div className="dropdown-header" onClick={toggleCatalog}>
                <span>Catalog</span>
                asd
                <ChevronDown
                  size={18}
                  style={{
                    transform: showCatalog ? "rotate(180deg)" : "rotate(0deg)",
                    transition: "transform 0.3s ease",
                  }}
                />
              </div>

              {/* Search Input */}
              <motion.div
                className={`search-container ${searchFocused ? "focused" : ""}`}
                whileHover={{ scale: 1.01 }}
                transition={{ type: "spring", stiffness: 400 }}
              >
                <input
                  type="text"
                  placeholder="Search products..."
                  onFocus={() => setSearchFocused(true)}
                  onBlur={() => setSearchFocused(false)}
                />
                <motion.div whileHover={{ scale: 1.2, rotate: 15 }} whileTap={{ scale: 0.9 }}>
                  <Search className="search-icon" size={20} />
                </motion.div>
              </motion.div>
            </div>
          </motion.div>

          {/* Actions */}
          <motion.div
            className="header-actions"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <div className="actions">
              <motion.div whileHover={{ scale: 1.15, y: -2 }} whileTap={{ scale: 0.95 }}>
                <User className="action-icon" size={22} />
              </motion.div>

              <motion.div className="cart-wrapper" whileHover={{ scale: 1.15, y: -2 }} whileTap={{ scale: 0.95 }}>
                <ShoppingCart className="action-icon" size={22} />
                <motion.span
                  className="cart-badge"
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  transition={{ delay: 0.6, type: "spring", stiffness: 500 }}
                >
                  3
                </motion.span>
              </motion.div>

              <motion.button
                className="sell-now-btn"
                whileHover={{ scale: 1.05, y: -2 }}
                whileTap={{ scale: 0.95 }}
                transition={{ type: "spring", stiffness: 400 }}
              >
                <motion.span animate={{ x: [0, 3, 0] }} transition={{ duration: 1.5, repeat: Infinity }}>
                  Sell Now
                </motion.span>
              </motion.button>
            </div>
          </motion.div>
        </div>
      </motion.div>

      {/* =========================
          CATALOG DROPDOWN OUTSIDE HEADER
          ========================= */}
      <AnimatePresence>
        {showCatalog && (
          <motion.div
            className="dropdown-menu"
            style={{
              position: "fixed",
              top: dropdownPosition.top,
              left: dropdownPosition.left,
              zIndex: 9999,
              minWidth: 200,
            }}
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -10 }}
            transition={{ duration: 0.2 }}
          >
            {catalogItems.map((item) => (
              <div
                key={item.name}
                className="dropdown-item"
                onClick={() => setShowCatalog(false)}
              >
                <span className="catalog-icon">{item.icon}</span>
                {item.name}
              </div>
            ))}
          </motion.div>
        )}
      </AnimatePresence>
    </>
  )
}
