  const [showCatalog, setShowCatalog] = useState(false)
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false)
  const [searchFocused, setSearchFocused] = useState(false)
  const dropdownRef = useRef(null)

  const catalogItems = [
    { name: "Electronics", icon: <Zap size={18} /> },
    { name: "Fashion", icon: <Shirt size={18} /> },
    { name: "Home & Garden", icon: <Home size={18} /> },
    { name: "Sports", icon: <Dumbbell size={18} /> },
    { name: "Books", icon: <Book size={18} /> },
  ]

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setShowCatalog(false)
      }
    }
    document.addEventListener("mousedown", handleClickOutside)
    return () => document.removeEventListener("mousedown", handleClickOutside)
  }, [])

  useEffect(() => {
    if (mobileMenuOpen) {
      document.body.style.overflow = "hidden"
    } else {
      document.body.style.overflow = "unset"
    }
  }, [mobileMenuOpen])

  return (
    <>
      <header className="header-enhanced">
        <div className="header-content">
          {/* Logo */}
          <div className="logo-container">
            <Package className="logo-icon" size={28} />
            <span className="logo-text">Trustee</span>
          </div>

          {/* Desktop Navigation */}
          <div className="desktop-nav">
            {/* Catalog Dropdown */}
            <div className="catalog-dropdown" ref={dropdownRef}>
              <button
                className={`catalog-btn ${showCatalog ? "active" : ""}`}
                onClick={() => setShowCatalog(!showCatalog)}
              >
                <span>Catalog</span>
                <ChevronDown
                  size={18}
                  className={`chevron ${showCatalog ? "rotate" : ""}`}
                />
              </button>

              <div className={`dropdown-menu ${showCatalog ? "show" : ""}`}>
                {catalogItems.map((item, idx) => (
                  <button
                    key={idx}
                    className="dropdown-item"
                    onClick={() => {
                      console.log("Selected:", item.name)
                      setShowCatalog(false)
                    }}
                  >
                    <span className="item-icon">{item.icon}</span>
                    <span>{item.name}</span>
                  </button>
                ))}
              </div>
            </div>

            {/* Search Bar */}
            <div className={`search-container ${searchFocused ? "focused" : ""}`}>
              <Search className="search-icon" size={20} />
              <input
                type="text"
                placeholder="Search products..."
                className="search-input"
                onFocus={() => setSearchFocused(true)}
                onBlur={() => setSearchFocused(false)}
              />
            </div>

            {/* Action Buttons */}
            <div className="action-buttons">
              <button className="icon-btn" aria-label="User account">
                <User size={20} />
              </button>
              <button className="icon-btn cart-btn" aria-label="Shopping cart">
                <ShoppingCart size={20} />
                <span className="cart-badge">3</span>
              </button>
              <button className="primary-btn">
                Sign In
              </button>
            </div>
          </div>

          {/* Mobile Hamburger */}
          <button
            className="hamburger"
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            aria-label="Toggle menu"
          >
            {mobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
          </button>
        </div>
      </header>

      {/* Mobile Menu Overlay */}
      <div className={`mobile-overlay ${mobileMenuOpen ? "show" : ""}`}>
        <div className={`mobile-menu ${mobileMenuOpen ? "show" : ""}`}>
          {/* Search in Mobile */}
          <div className="mobile-search">
            <Search className="search-icon" size={20} />
            <input
              type="text"
              placeholder="Search products..."
              className="search-input"
            />
          </div>

          {/* Catalog Items */}
          <div className="mobile-section">
            <h3 className="section-title">Categories</h3>
            {catalogItems.map((item, idx) => (
              <button
                key={idx}
                className="mobile-item"
                onClick={() => {
                  console.log("Selected:", item.name)
                  setMobileMenuOpen(false)
                }}
              >
                <span className="item-icon">{item.icon}</span>
                <span>{item.name}</span>
              </button>
            ))}
          </div>

          {/* Mobile Actions */}
          <div className="mobile-actions">
            <button className="mobile-action-btn">
              <User size={20} />
              <span>My Account</span>
            </button>
            <button className="mobile-action-btn">
              <ShoppingCart size={20} />
              <span>Cart (3)</span>
            </button>
            <button className="mobile-primary-btn">
              Sign In / Sign Up
            </button>
          </div>
        </div>
      </div>

      <style jsx>{`
        * {
          box-sizing: border-box;
          margin: 0;
          padding: 0;
        }

        .header-enhanced {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          background: #ffffff;
          box-shadow: 0 2px 20px rgba(0, 0, 0, 0.08);
          z-index: 1000;
          animation: slideDown 0.4s ease-out;
        }

        @keyframes slideDown {
          from {
            transform: translateY(-100%);
            opacity: 0;
          }
          to {
            transform: translateY(0);
            opacity: 1;
          }
        }

        .header-content {
          max-width: 1400px;
          margin: 0 auto;
          padding: 1rem 2rem;
          display: flex;
          align-items: center;
          justify-content: space-between;
          gap: 2rem;
        }

        /* Logo */
        .logo-container {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          cursor: pointer;
          transition: transform 0.3s ease;
        }

        .logo-container:hover {
          transform: scale(1.05);
        }

        .logo-icon {
          color: #007bff;
          animation: rotate 3s linear infinite;
        }

        @keyframes rotate {
          0%, 90%, 100% {
            transform: rotate(0deg);
          }
          95% {
            transform: rotate(360deg);
          }
        }

        .logo-text {
          font-family: "Orbitron", sans-serif;
          font-weight: 700;
          font-size: 1.5rem;
          background: linear-gradient(135deg, #007bff, #0056b3);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }

        /* Desktop Nav */
        .desktop-nav {
          display: flex;
          align-items: center;
          gap: 1.5rem;
          flex: 1;
        }

        /* Catalog Dropdown */
        .catalog-dropdown {
          position: relative;
        }

        .catalog-btn {
          display: flex;
          align-items: center;
          gap: 0.5rem;
          padding: 0.6rem 1.2rem;
          background: #f8f9fa;
          border: 2px solid transparent;
          border-radius: 12px;
          cursor: pointer;
          font-weight: 500;
          transition: all 0.3s ease;
        }

        .catalog-btn:hover {
          background: #e9ecef;
          border-color: #007bff;
        }

        .catalog-btn.active {
          background: #007bff;
          color: white;
          border-color: #007bff;
        }

        .chevron {
          transition: transform 0.3s ease;
        }

        .chevron.rotate {
          transform: rotate(180deg);
        }

        .dropdown-menu {
          position: absolute;
          top: calc(100% + 0.5rem);
          left: 0;
          background: white;
          border-radius: 12px;
          box-shadow: 0 10px 40px rgba(0, 0, 0, 0.15);
          min-width: 200px;
          opacity: 0;
          visibility: hidden;
          transform: translateY(-10px);
          transition: all 0.3s cubic-bezier(0.68, -0.55, 0.265, 1.55);
          overflow: hidden;
        }

        .dropdown-menu.show {
          opacity: 1;
          visibility: visible;
          transform: translateY(0);
        }

        .dropdown-item {
          display: flex;
          align-items: center;
          gap: 0.8rem;
          width: 100%;
          padding: 0.9rem 1.2rem;
          background: none;
          border: none;
          cursor: pointer;
          transition: all 0.2s ease;
          font-size: 0.95rem;
        }

        .dropdown-item:hover {
          background: linear-gradient(90deg, #e3f2fd, #bbdefb);
          color: #0056b3;
          transform: translateX(5px);
        }

        .item-icon {
          display: flex;
          color: #007bff;
        }

        /* Search Bar */
        .search-container {
          position: relative;
          flex: 1;
          max-width: 500px;
          transition: all 0.3s ease;
        }

        .search-container.focused {
          transform: scale(1.02);
        }

        .search-icon {
          position: absolute;
          left: 1rem;
          top: 50%;
          transform: translateY(-50%);
          color: #6c757d;
          pointer-events: none;
          transition: color 0.3s ease;
        }

        .search-container.focused .search-icon {
          color: #007bff;
        }

        .search-input {
          width: 100%;
          padding: 0.7rem 1rem 0.7rem 2.8rem;
          border: 2px solid #e9ecef;
          border-radius: 25px;
          outline: none;
          font-size: 0.95rem;
          transition: all 0.3s ease;
        }

        .search-input:focus {
          border-color: #007bff;
          box-shadow: 0 0 0 4px rgba(0, 123, 255, 0.1);
        }

        /* Action Buttons */
        .action-buttons {
          display: flex;
          align-items: center;
          gap: 0.8rem;
        }

        .icon-btn {
          position: relative;
          display: flex;
          align-items: center;
          justify-content: center;
          width: 42px;
          height: 42px;
          background: #f8f9fa;
          border: 2px solid transparent;
          border-radius: 50%;
          cursor: pointer;
          transition: all 0.3s ease;
        }

        .icon-btn:hover {
          background: #007bff;
          color: white;
          transform: translateY(-2px);
          box-shadow: 0 5px 15px rgba(0, 123, 255, 0.3);
        }

        .cart-badge {
          position: absolute;
          top: -5px;
          right: -5px;
          background: linear-gradient(135deg, #ff416c, #ff4b2b);
          color: white;
          width: 20px;
          height: 20px;
          border-radius: 50%;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 0.7rem;
          font-weight: 700;
          animation: pulse 2s infinite;
        }

        @keyframes pulse {
          0%, 100% {
            transform: scale(1);
          }
          50% {
            transform: scale(1.1);
          }
        }

        .primary-btn {
          padding: 0.7rem 1.5rem;
          background: linear-gradient(135deg, #007bff, #0056b3);
          color: white;
          border: none;
          border-radius: 25px;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease;
          white-space: nowrap;
        }

        .primary-btn:hover {
          transform: translateY(-2px);
          box-shadow: 0 8px 20px rgba(0, 123, 255, 0.4);
        }

        /* Hamburger */
        .hamburger {
          display: none;
          background: none;
          border: none;
          cursor: pointer;
          padding: 0.5rem;
          transition: transform 0.3s ease;
        }

        .hamburger:hover {
          transform: scale(1.1);
        }

        /* Mobile Overlay */
        .mobile-overlay {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background: rgba(0, 0, 0, 0.5);
          z-index: 999;
          opacity: 0;
          visibility: hidden;
          transition: all 0.3s ease;
          backdrop-filter: blur(5px);
        }

        .mobile-overlay.show {
          opacity: 1;
          visibility: visible;
        }

        .mobile-menu {
          position: fixed;
          top: 0;
          right: 0;
          bottom: 0;
          width: 85%;
          max-width: 400px;
          background: white;
          padding: 5rem 2rem 2rem;
          overflow-y: auto;
          transform: translateX(100%);
          transition: transform 0.4s cubic-bezier(0.68, -0.55, 0.265, 1.55);
          box-shadow: -5px 0 25px rgba(0, 0, 0, 0.2);
        }

        .mobile-menu.show {
          transform: translateX(0);
        }

        .mobile-search {
          position: relative;
          margin-bottom: 2rem;
        }

        .mobile-search .search-icon {
          position: absolute;
          left: 1rem;
          top: 50%;
          transform: translateY(-50%);
          color: #6c757d;
        }

        .mobile-search .search-input {
          width: 100%;
          padding: 0.9rem 1rem 0.9rem 2.8rem;
          border: 2px solid #e9ecef;
          border-radius: 12px;
          outline: none;
        }

        .mobile-section {
          margin-bottom: 2rem;
        }

        .section-title {
          font-size: 0.85rem;
          font-weight: 700;
          color: #6c757d;
          text-transform: uppercase;
          letter-spacing: 1px;
          margin-bottom: 1rem;
        }

        .mobile-item {
          display: flex;
          align-items: center;
          gap: 1rem;
          width: 100%;
          padding: 1rem;
          background: none;
          border: none;
          border-radius: 12px;
          cursor: pointer;
          transition: all 0.3s ease;
          margin-bottom: 0.5rem;
        }

        .mobile-item:hover {
          background: linear-gradient(90deg, #e3f2fd, #bbdefb);
          transform: translateX(5px);
        }

        .mobile-actions {
          display: flex;
          flex-direction: column;
          gap: 0.8rem;
          margin-top: 2rem;
          padding-top: 2rem;
          border-top: 2px solid #e9ecef;
        }

        .mobile-action-btn {
          display: flex;
          align-items: center;
          gap: 1rem;
          width: 100%;
          padding: 1rem;
          background: #f8f9fa;
          border: 2px solid transparent;
          border-radius: 12px;
          cursor: pointer;
          font-weight: 500;
          transition: all 0.3s ease;
        }

        .mobile-action-btn:hover {
          background: white;
          border-color: #007bff;
          color: #007bff;
        }

        .mobile-primary-btn {
          width: 100%;
          padding: 1rem;
          background: linear-gradient(135deg, #007bff, #0056b3);
          color: white;
          border: none;
          border-radius: 12px;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease;
          margin-top: 0.5rem;
        }

        .mobile-primary-btn:hover {
          transform: translateY(-2px);
          box-shadow: 0 8px 20px rgba(0, 123, 255, 0.4);
        }

        /* Responsive */
        @media (max-width: 968px) {
          .desktop-nav {
            display: none;
          }

          .hamburger {
            display: block;
          }

          .header-content {
            padding: 1rem 1.5rem;
          }
        }

        @media (max-width: 480px) {
          .logo-text {
            font-size: 1.3rem;
          }

          .mobile-menu {
            width: 90%;
          }
        }
      `}</style>
    </>
  )
}
