import { React, useState, useEffect } from 'react'

import { Col } from 'react-bootstrap'

import '../../styles/landingpage/landingpage.scss'

import Header from './header-component.js'

export default function LandingPage() {
  const [viewport, setViewport] = useState('md');

  useEffect(() => {
    const handleResize = () => {
      setViewport(window.innerWidth <= 600 ? 'xs' : 'md');
    };
    handleResize();
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div style={{ minHeight: '200vh', background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)' }}>
      <Header viewport={viewport} />
      <div style={{ padding: '2rem', marginTop: '100px' }}>
        <h1>Scroll down to see header effects</h1>
        <p>Current viewport: <strong>{viewport}</strong></p>
      </div>
    </div>
  );
}
