"use client"

import { useState, useEffect, useRef } from "react"
import { motion, AnimatePresence } from "framer-motion"
import { Menu, Search, User, ShoppingCart, ChevronDown, X, Sparkles, Store, HelpCircle } from "lucide-react"
import '../../styles/landingpage/header.scss'

export default function Header({ viewport = "md" }) {
  const [showCatalog, setShowCatalog] = useState(false)
  const [showMenu, setShowMenu] = useState(false)
  const [scrolled, setScrolled] = useState(false)
  const [searchFocused, setSearchFocused] = useState(false)
  const dropdownRef = useRef(null)

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 20)
    }
    window.addEventListener("scroll", handleScroll)
    return () => window.removeEventListener("scroll", handleScroll)
  }, [])

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowCatalog(false)
      }
    }

    if (showCatalog) {
      document.addEventListener("mousedown", handleClickOutside)
    }

    return () => {
      document.removeEventListener("mousedown", handleClickOutside)
    }
  }, [showCatalog])

  const toggleCatalog = () => {
    setShowCatalog((prev) => !prev)
  }

  const toggleMenu = () => setShowMenu((prev) => !prev)
  const closeMenu = () => setShowMenu(false)

  const catalogItems = [
    { name: "Electronics", icon: "üíª" },
    { name: "Fashion", icon: "üëï" },
    { name: "Home & Garden", icon: "üè°" },
    { name: "Sports", icon: "‚öΩ" },
    { name: "Books", icon: "üìö" },
  ]

  const mobileMenuItems = [
    { name: "Catalog", Icon: ChevronDown },
    { name: "Sellers", Icon: Store },
    { name: "Help Center", Icon: HelpCircle },
    { name: "Sign in / Sign up", Icon: User },
    { name: "Sell Now", Icon: Sparkles, highlight: true },
  ]

  // üîπ MOBILE HEADER
  if (viewport === "xs") {
    return (
      <motion.div
        className={`fixed top-0 left-0 right-0 z-50 bg-white border-b-2 border-gray-100 p-4 transition-all ${scrolled ? "shadow-lg" : ""}`}
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ type: "spring", stiffness: 100, damping: 20 }}
      >
        <div className="flex items-center justify-between">
          <motion.h3
            className="text-2xl font-bold text-blue-500 tracking-wider flex items-center gap-2"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            whileHover={{ scale: 1.05 }}
          >
            <Sparkles size={20} className="text-yellow-400" />
            Trustee
          </motion.h3>

          <motion.div
            whileHover={{ scale: 1.1, rotate: 90 }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            <Menu size={28} className="text-blue-500 cursor-pointer" onClick={toggleMenu} />
          </motion.div>
        </div>

        {/* Mobile Menu Overlay */}
        <AnimatePresence>
          {showMenu && (
            <>
              <motion.div
                className="fixed top-0 left-0 right-0 bottom-0 bg-black/50 z-40"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                onClick={closeMenu}
              />
              <motion.div
                className="fixed top-0 right-0 bg-white w-85% max-w-sm h-screen shadow-lg z-50 flex flex-col"
                initial={{ x: "100%" }}
                animate={{ x: 0 }}
                exit={{ x: "100%" }}
                transition={{ type: "spring", stiffness: 100, damping: 20 }}
              >
                <div className="flex justify-between items-center p-6 border-b-2 border-gray-100 bg-gradient-to-r from-blue-50 to-white">
                  <motion.h4
                    className="text-xl font-bold text-blue-500 font-mono tracking-wider"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.2 }}
                  >
                    Menu
                  </motion.h4>
                  <motion.button
                    className="bg-white border-2 border-blue-500 rounded-full w-10 h-10 flex items-center justify-center text-blue-500 transition-all"
                    onClick={closeMenu}
                    whileHover={{ scale: 1.1, rotate: 90 }}
                    whileTap={{ scale: 0.9 }}
                  >
                    <X size={24} />
                  </motion.button>
                </div>

                <div className="flex-1 p-6 flex flex-col gap-2">
                  {mobileMenuItems.map((item, idx) => {
                    const IconComponent = item.Icon
                    return (
                      <motion.div
                        key={item.name}
                        className={`flex items-center gap-4 text-lg font-medium p-4 rounded-lg transition-all ${
                          item.highlight
                            ? "bg-gradient-to-r from-blue-500 to-blue-600 text-white shadow-lg"
                            : "text-gray-800 hover:bg-blue-50"
                        }`}
                        initial={{ x: 50, opacity: 0 }}
                        animate={{ x: 0, opacity: 1 }}
                        transition={{ delay: idx * 0.1 }}
                        whileHover={{ x: 10 }}
                      >
                        <IconComponent size={20} className={item.highlight ? "text-white" : "text-blue-500"} />
                        <span>{item.name}</span>
                      </motion.div>
                    )
                  })}
                </div>
              </motion.div>
            </>
          )}
        </AnimatePresence>
      </motion.div>
    )
  }

  // üîπ DESKTOP HEADER
  return (
    <motion.div
      className={`fixed top-0 left-0 right-0 z-50 bg-white border-b-2 border-gray-100 px-8 py-4 transition-all ${
        scrolled ? "shadow-lg" : ""
      }`}
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ type: "spring", stiffness: 100, damping: 20 }}
    >
      <div className="max-w-7xl mx-auto flex items-center justify-between gap-8">
        {/* Business Name */}
        <motion.div
          className="flex-shrink-0"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
        >
          <motion.h3
            className="text-3xl font-bold text-blue-500 tracking-wider font-mono flex items-center gap-2 hover:text-blue-600 cursor-pointer transition-colors"
            whileHover={{ scale: 1.05 }}
            transition={{ type: "spring", stiffness: 400 }}
          >
            <motion.span
              animate={{ rotate: [0, 360] }}
              transition={{ duration: 2, repeat: Number.POSITIVE_INFINITY, ease: "linear" }}
              style={{ display: "inline-block" }}
            >
              <Sparkles size={28} className="text-yellow-400 drop-shadow-lg" />
            </motion.span>
            Trustee
          </motion.h3>
        </motion.div>

        {/* Search Area */}
        <motion.div
          className="flex-1 max-w-2xl"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          <div className="flex items-center gap-4 h-11">
            {/* Catalog Dropdown */}
            <div className="relative z-50" ref={dropdownRef}>
              <motion.div
                className="flex items-center justify-between gap-2 border border-gray-300 px-5 py-2 rounded-lg bg-gray-50 cursor-pointer hover:bg-blue-50 hover:border-blue-500 transition-all font-medium whitespace-nowrap"
                onClick={toggleCatalog}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
              >
                <span className="text-gray-800">Catalog</span>
                <motion.div animate={{ rotate: showCatalog ? 180 : 0 }} transition={{ duration: 0.3 }}>
                  <ChevronDown size={18} className="text-gray-600" />
                </motion.div>
              </motion.div>

              <AnimatePresence>
                {showCatalog && (
                  <motion.div
                    className="absolute top-full left-0 bg-white border border-gray-300 rounded-xl min-w-48 mt-2 shadow-lg z-50"
                    initial={{ opacity: 0, y: -10, scale: 0.95 }}
                    animate={{ opacity: 1, y: 0, scale: 1 }}
                    exit={{ opacity: 0, y: -10, scale: 0.95 }}
                    transition={{ duration: 0.2 }}
                  >
                    {catalogItems.map((item, idx) => (
                      <motion.p
                        key={item.name}
                        className="m-0 p-3 text-sm cursor-pointer flex items-center gap-3 text-gray-800 hover:bg-blue-50 hover:text-blue-600 transition-all hover:pl-4"
                        initial={{ x: -20, opacity: 0 }}
                        animate={{ x: 0, opacity: 1 }}
                        transition={{ delay: idx * 0.05 }}
                        whileHover={{ x: 5 }}
                      >
                        <span className="text-lg">{item.icon}</span>
                        {item.name}
                      </motion.p>
                    ))}
                  </motion.div>
                )}
              </AnimatePresence>
            </div>

            {/* Search Input */}
            <motion.div
              className={`relative flex-1 transition-all ${searchFocused ? "ring-2 ring-blue-500" : ""}`}
              whileHover={{ scale: 1.01 }}
              transition={{ type: "spring", stiffness: 400 }}
            >
              <input
                type="text"
                placeholder="Search products..."
                className="w-full px-4 py-2 pr-10 border-2 border-gray-300 rounded-full focus:outline-none focus:border-blue-500 transition-all"
                onFocus={() => setSearchFocused(true)}
                onBlur={() => setSearchFocused(false)}
              />
              <motion.div
                className="absolute right-3 top-1/2 -translate-y-1/2 text-blue-500 cursor-pointer"
                whileHover={{ scale: 1.2, rotate: 15 }}
                whileTap={{ scale: 0.9 }}
              >
                <Search size={20} />
              </motion.div>
            </motion.div>
          </div>
        </motion.div>

        {/* Actions */}
        <motion.div
          className="flex-shrink-0"
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
        >
          <div className="flex items-center gap-6">
            <motion.div
              className="text-blue-500 cursor-pointer hover:text-blue-600 transition-colors"
              whileHover={{ scale: 1.15, y: -2 }}
              whileTap={{ scale: 0.95 }}
            >
              <User size={22} />
            </motion.div>

            <motion.div
              className="relative text-blue-500 cursor-pointer hover:text-blue-600 transition-colors"
              whileHover={{ scale: 1.15, y: -2 }}
              whileTap={{ scale: 0.95 }}
            >
              <ShoppingCart size={22} />
              <motion.span
                className="absolute -top-2 -right-2 bg-gradient-to-r from-red-500 to-red-600 text-white text-xs font-bold w-5 h-5 rounded-full flex items-center justify-center shadow-lg"
                initial={{ scale: 0 }}
                animate={{ scale: 1 }}
                transition={{ delay: 0.6, type: "spring", stiffness: 500 }}
              >
                3
              </motion.span>
            </motion.div>

            <motion.button
              className="bg-gradient-to-r from-blue-500 to-blue-600 text-white px-6 py-2 rounded-full font-semibold hover:shadow-lg transition-all"
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.95 }}
              transition={{ type: "spring", stiffness: 400 }}
            >
              <motion.span animate={{ x: [0, 3, 0] }} transition={{ duration: 1.5, repeat: Number.POSITIVE_INFINITY }}>
                Sell Now
              </motion.span>
            </motion.button>
          </div>
        </motion.div>
      </div>
    </motion.div>
  )
}
