"use client"

import { useState, useEffect, useRef } from "react"
import { motion, AnimatePresence } from "framer-motion"
import { Menu, Search, User, ShoppingCart, ChevronDown, X, Sparkles, Store, HelpCircle } from "lucide-react"
import "../../styles/landingpage/header.scss"

export default function Header({ viewport }) {
  const [showCatalog, setShowCatalog] = useState(true)
  const [showMenu, setShowMenu] = useState(false)
  const [scrolled, setScrolled] = useState(false)
  const [searchFocused, setSearchFocused] = useState(false)
  const dropdownRef = useRef(null)

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 20)
    }
    window.addEventListener("scroll", handleScroll)
    return () => window.removeEventListener("scroll", handleScroll)
  }, [])

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowCatalog(false)
      }
    }

    if (showCatalog) {
      document.addEventListener("mousedown", handleClickOutside)
    }

    return () => {
      document.removeEventListener("mousedown", handleClickOutside)
    }
  }, [showCatalog])

  const toggleCatalog = (e) => {
    e.stopPropagation()
    setShowCatalog((prev) => !prev)
  }

  const toggleMenu = () => setShowMenu((prev) => !prev)
  const closeMenu = () => setShowMenu(false)

  const catalogItems = [
    { name: "Electronics", icon: "üíª" },
    { name: "Fashion", icon: "üëï" },
    { name: "Home & Garden", icon: "üè°" },
    { name: "Sports", icon: "‚öΩ" },
    { name: "Books", icon: "üìö" },
  ]

  const mobileMenuItems = [
    { name: "Catalog", Icon: ChevronDown },
    { name: "Sellers", Icon: Store },
    { name: "Help Center", Icon: HelpCircle },
    { name: "Sign in / Sign up", Icon: User },
    { name: "Sell Now", Icon: Sparkles, highlight: true },
  ]

  // üîπ MOBILE HEADER
  if (viewport === "xs") {
    return (
      <motion.div
        className={`header-mobile ${scrolled ? "scrolled" : ""}`}
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ type: "spring", stiffness: 100, damping: 20 }}
      >
        <div className="header-mobile-content">
          <motion.h3
            className="header-title"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            whileHover={{ scale: 1.05 }}
          >
            <Sparkles size={20} className="title-sparkle" />
            Trustee
          </motion.h3>

          <motion.div
            whileHover={{ scale: 1.1, rotate: 90 }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            <Menu size={28} className="menu-icon" onClick={toggleMenu} />
          </motion.div>
        </div>

        {/* Mobile Menu Overlay */}
        <AnimatePresence>
          {showMenu && (
            <>
              <motion.div
                className="menu-overlay"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                onClick={closeMenu}
              />
              <motion.div
                className="header-hamburger-dropdown"
                initial={{ x: "100%" }}
                animate={{ x: 0 }}
                exit={{ x: "100%" }}
                transition={{ type: "spring", stiffness: 100, damping: 20 }}
              >
                <div className="menu-header">
                  <motion.h4 initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.2 }}>
                    Menu
                  </motion.h4>
                  <motion.button
                    className="close-btn"
                    onClick={closeMenu}
                    whileHover={{ scale: 1.1, rotate: 90 }}
                    whileTap={{ scale: 0.9 }}
                  >
                    <X size={24} />
                  </motion.button>
                </div>

                <div className="menu-items">
                  {mobileMenuItems.map((item, idx) => {
                    const IconComponent = item.Icon
                    return (
                      <motion.div
                        key={item.name}
                        className={`menu-item ${item.highlight ? "highlight" : ""}`}
                        initial={{ x: 50, opacity: 0 }}
                        animate={{ x: 0, opacity: 1 }}
                        transition={{ delay: idx * 0.1 }}
                        whileHover={{ x: 10 }}
                      >
                        <IconComponent size={20} className="menu-item-icon" />
                        <span>{item.name}</span>
                      </motion.div>
                    )
                  })}
                </div>
              </motion.div>
            </>
          )}
        </AnimatePresence>
      </motion.div>
    )
  }

  // üîπ DESKTOP HEADER
  return (
   <div className="header-desktop">
      <div className="header-content">
        <div className="header-search" ref={dropdownRef}>
          <div className="dropdown-header" onClick={toggleCatalog}>
            <span>Catalog</span>
            <ChevronDown style={{ transform: showCatalog ? "rotate(180deg)" : "rotate(0deg)", transition: "0.3s" }} />
          </div>
        </div>
      </div>

      {showCatalog && (
        <div
          className="dropdown-menu"
          style={{
            position: "fixed",
            top: dropdownPosition.top,
            left: dropdownPosition.left,
            zIndex: 9999,
          }}
        >
          {catalogItems.map(item => (
            <div key={item.name} className="dropdown-item" onClick={() => setShowCatalog(false)}>
              <span className="catalog-icon">{item.icon}</span>
              {item.name}
            </div>
          ))}
        </div>
      )}
    </div>
  )
}
