"use client"

import { useState, useEffect, useRef } from "react"
import { motion, AnimatePresence } from "framer-motion"
import { Menu, Search, User, ShoppingCart, ChevronDown, X, Sparkles, Store, HelpCircle } from "lucide-react"
import "../styles/landingpage/header.css"

export default function Header({ viewport }) {
  const [showCatalog, setShowCatalog] = useState(false)
  const [showMenu, setShowMenu] = useState(false)
  const [scrolled, setScrolled] = useState(false)
  const [searchFocused, setSearchFocused] = useState(false)
  const dropdownRef = useRef(null)

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 20)
    }
    window.addEventListener("scroll", handleScroll)
    return () => window.removeEventListener("scroll", handleScroll)
  }, [])

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowCatalog(false)
      }
    }

    if (showCatalog) {
      document.addEventListener("mousedown", handleClickOutside)
    }

    return () => {
      document.removeEventListener("mousedown", handleClickOutside)
    }
  }, [showCatalog])

  const toggleCatalog = (e) => {
    e.stopPropagation()
    setShowCatalog((prev) => !prev)
  }

  const toggleMenu = () => setShowMenu((prev) => !prev)
  const closeMenu = () => setShowMenu(false)

  const catalogItems = [
    { name: "Electronics", icon: "üíª" },
    { name: "Fashion", icon: "üëï" },
    { name: "Home & Garden", icon: "üè°" },
    { name: "Sports", icon: "‚öΩ" },
    { name: "Books", icon: "üìö" },
  ]

  const mobileMenuItems = [
    { name: "Catalog", Icon: ChevronDown },
    { name: "Sellers", Icon: Store },
    { name: "Help Center", Icon: HelpCircle },
    { name: "Sign in / Sign up", Icon: User },
    { name: "Sell Now", Icon: Sparkles, highlight: true },
  ]

  // üîπ MOBILE HEADER
  if (viewport === "xs") {
    return (
      <motion.div
        className={`header-mobile ${scrolled ? "scrolled" : ""}`}
        initial={{ y: -100 }}
        animate={{ y: 0 }}
        transition={{ type: "spring", stiffness: 100, damping: 20 }}
      >
        <div className="header-mobile-content">
          <motion.h3
            className="header-title"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            whileHover={{ scale: 1.05 }}
          >
            <Sparkles size={20} className="title-sparkle" />
            Trustee
          </motion.h3>

          <motion.div
            whileHover={{ scale: 1.1, rotate: 90 }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            <Menu size={28} className="menu-icon" onClick={toggleMenu} />
          </motion.div>
        </div>

        {/* Mobile Menu Overlay */}
        <AnimatePresence>
          {showMenu && (
            <>
              <motion.div
                className="menu-overlay"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                exit={{ opacity: 0 }}
                onClick={closeMenu}
              />
              <motion.div
                className="header-hamburger-dropdown"
                initial={{ x: "100%" }}
                animate={{ x: 0 }}
                exit={{ x: "100%" }}
                transition={{ type: "spring", stiffness: 100, damping: 20 }}
              >
                <div className="menu-header">
                  <motion.h4 initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.2 }}>
                    Menu
                  </motion.h4>
                  <motion.button
                    className="close-btn"
                    onClick={closeMenu}
                    whileHover={{ scale: 1.1, rotate: 90 }}
                    whileTap={{ scale: 0.9 }}
                  >
                    <X size={24} />
                  </motion.button>
                </div>

                <div className="menu-items">
                  {mobileMenuItems.map((item, idx) => {
                    const IconComponent = item.Icon
                    return (
                      <motion.div
                        key={item.name}
                        className={`menu-item ${item.highlight ? "highlight" : ""}`}
                        initial={{ x: 50, opacity: 0 }}
                        animate={{ x: 0, opacity: 1 }}
                        transition={{ delay: idx * 0.1 }}
                        whileHover={{ x: 10 }}
                      >
                        <IconComponent size={20} className="menu-item-icon" />
                        <span>{item.name}</span>
                      </motion.div>
                    )
                  })}
                </div>
              </motion.div>
            </>
          )}
        </AnimatePresence>
      </motion.div>
    )
  }

  // üîπ DESKTOP HEADER
  return (
    <motion.div
      className={`header-desktop ${scrolled ? "scrolled" : ""}`}
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ type: "spring", stiffness: 100, damping: 20 }}
    >
      <div className="header-content">
        {/* Business Name */}
        <motion.div
          className="header-business"
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
        >
          <motion.h3
            className="header-title"
            whileHover={{ scale: 1.05 }}
            transition={{ type: "spring", stiffness: 400 }}
          >
            <motion.span
              animate={{ rotate: [0, 360] }}
              transition={{ duration: 2, repeat: Number.POSITIVE_INFINITY, ease: "linear" }}
              style={{ display: "inline-block" }}
            >
              <Sparkles size={24} className="title-sparkle" />
            </motion.span>
            Trustee
          </motion.h3>
        </motion.div>

        {/* Search Area */}
        <motion.div
          className="header-search"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          <div className="search-wrapper">
            {/* Catalog Dropdown - Using regular div */}
            <div className="dropdown-container" ref={dropdownRef}>
              <div className="dropdown-header" onClick={toggleCatalog}>
                <span>Catalog</span>
                <ChevronDown
                  size={18}
                  style={{
                    transform: showCatalog ? "rotate(180deg)" : "rotate(0deg)",
                    transition: "transform 0.3s ease",
                  }}
                />
              </div>

              {/* Regular div dropdown instead of AnimatePresence */}
              {showCatalog && (
                <div className="dropdown-menu">
                  {catalogItems.map((item, idx) => (
                    <div
                      key={item.name}
                      className="dropdown-item"
                      onClick={() => {
                        console.log(`Selected: ${item.name}`)
                        setShowCatalog(false)
                      }}
                    >
                      <span className="catalog-icon">{item.icon}</span>
                      {item.name}
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* Search Input */}
            <motion.div
              className={`search-container ${searchFocused ? "focused" : ""}`}
              whileHover={{ scale: 1.01 }}
              transition={{ type: "spring", stiffness: 400 }}
            >
              <input
                type="text"
                placeholder="Search products..."
                onFocus={() => setSearchFocused(true)}
                onBlur={() => setSearchFocused(false)}
              />
              <motion.div whileHover={{ scale: 1.2, rotate: 15 }} whileTap={{ scale: 0.9 }}>
                <Search className="search-icon" size={20} />
              </motion.div>
            </motion.div>
          </div>
        </motion.div>

        {/* Actions */}
        <motion.div
          className="header-actions"
          initial={{ opacity: 0, x: 50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
        >
          <div className="actions">
            <motion.div whileHover={{ scale: 1.15, y: -2 }} whileTap={{ scale: 0.95 }}>
              <User className="action-icon" size={22} />
            </motion.div>

            <motion.div className="cart-wrapper" whileHover={{ scale: 1.15, y: -2 }} whileTap={{ scale: 0.95 }}>
              <ShoppingCart className="action-icon" size={22} />
              <motion.span
                className="cart-badge"
                initial={{ scale: 0 }}
                animate={{ scale: 1 }}
                transition={{ delay: 0.6, type: "spring", stiffness: 500 }}
              >
                3
              </motion.span>
            </motion.div>

            <motion.button
              className="sell-now-btn"
              whileHover={{ scale: 1.05, y: -2 }}
              whileTap={{ scale: 0.95 }}
              transition={{ type: "spring", stiffness: 400 }}
            >
              <motion.span animate={{ x: [0, 3, 0] }} transition={{ duration: 1.5, repeat: Number.POSITIVE_INFINITY }}>
                Sell Now
              </motion.span>
            </motion.button>
          </div>
        </motion.div>
      </div>
    </motion.div>
  )
}
