import React, { useState, useEffect } from 'react';
import { Menu, Search, User, ShoppingCart, ChevronDown, X, LogIn, UserPlus } from 'lucide-react';

export default function Header({ viewport = 'md' }) {
  const [showCatalog, setShowCatalog] = useState(false);
  const [showMenu, setShowMenu] = useState(false);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 20);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    if (showMenu) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [showMenu]);

  const toggleCatalog = (e) => {
    e.stopPropagation();
    setShowCatalog(prev => !prev);
  };

  const toggleMenu = () => setShowMenu(prev => !prev);

  const catalogItems = [
    { label: 'Electronics', icon: 'üíª' },
    { label: 'Fashion', icon: 'üëï' },
    { label: 'Home & Garden', icon: 'üè°' },
    { label: 'Sports', icon: '‚öΩ' },
    { label: 'Books', icon: 'üìö' },
    { label: 'Toys', icon: 'üéÆ' }
  ];

  // Close dropdowns when clicking outside
  useEffect(() => {
    const handleClickOutside = () => {
      setShowCatalog(false);
    };
    if (showCatalog) {
      document.addEventListener('click', handleClickOutside);
    }
    return () => document.removeEventListener('click', handleClickOutside);
  }, [showCatalog]);

  // MOBILE HEADER
  if (viewport === 'xs') {
    return (
      <>
        <style>{styles}</style>
        <header className={`header ${scrolled ? 'scrolled' : ''}`}>
          <div className="header-mobile">
            <div className="logo-container">
              <h3 className="logo">Trustee</h3>
            </div>
            
            <button 
              className="menu-toggle" 
              onClick={toggleMenu}
              aria-label="Toggle menu"
            >
              <Menu size={28} />
            </button>
          </div>

          {/* Mobile Menu Overlay */}
          <div 
            className={`menu-overlay ${showMenu ? 'active' : ''}`}
            onClick={toggleMenu}
          />

          {/* Mobile Menu Sidebar */}
          <nav className={`mobile-menu ${showMenu ? 'active' : ''}`}>
            <div className="mobile-menu-header">
              <h3 className="logo">Trustee</h3>
              <button 
                className="close-btn" 
                onClick={toggleMenu}
                aria-label="Close menu"
              >
                <X size={28} />
              </button>
            </div>

            <div className="mobile-menu-content">
              <div className="mobile-menu-section">
                <h4>Browse</h4>
                <ul>
                  <li>
                    <span className="menu-icon">üì¶</span>
                    Catalog
                  </li>
                  <li>
                    <span className="menu-icon">üè™</span>
                    Sellers
                  </li>
                  <li>
                    <span className="menu-icon">‚ùì</span>
                    Help Center
                  </li>
                </ul>
              </div>

              <div className="mobile-menu-section">
                <h4>Account</h4>
                <ul>
                  <li>
                    <span className="menu-icon"><LogIn size={18} /></span>
                    Sign In
                  </li>
                  <li>
                    <span className="menu-icon"><UserPlus size={18} /></span>
                    Sign Up
                  </li>
                </ul>
              </div>

              <button className="sell-now-btn mobile">
                Sell Now
              </button>
            </div>
          </nav>
        </header>
      </>
    );
  }

  // DESKTOP HEADER
  return (
    <>
      <style>{styles}</style>
      <header className={`header ${scrolled ? 'scrolled' : ''}`}>
        <div className="header-desktop">
          {/* Logo */}
          <div className="logo-container">
            <h3 className="logo">Trustee</h3>
          </div>

          {/* Search Area */}
          <div className="search-wrapper">
            <div className="catalog-dropdown">
              <button 
                className="catalog-btn" 
                onClick={toggleCatalog}
              >
                <span>Catalog</span>
                <ChevronDown 
                  size={18} 
                  className={`chevron ${showCatalog ? 'rotated' : ''}`}
                />
              </button>

              <div className={`dropdown-menu ${showCatalog ? 'active' : ''}`}>
                {catalogItems.map((item, idx) => (
                  <div 
                    key={idx} 
                    className="dropdown-item"
                    style={{ animationDelay: `${idx * 0.05}s` }}
                  >
                    <span className="item-icon">{item.icon}</span>
                    {item.label}
                  </div>
                ))}
              </div>
            </div>

            <div className="search-container">
              <input 
                type="text" 
                placeholder="Search products..." 
                className="search-input"
              />
              <button className="search-btn" aria-label="Search">
                <Search size={20} />
              </button>
            </div>
          </div>

          {/* Actions */}
          <div className="actions-container">
            <button className="action-btn" aria-label="Account">
              <User size={22} />
              <span className="tooltip">Account</span>
            </button>
            
            <button className="action-btn cart-btn" aria-label="Cart">
              <ShoppingCart size={22} />
              <span className="cart-badge">3</span>
              <span className="tooltip">Cart</span>
            </button>
            
            <button className="sell-now-btn">
              Sell Now
            </button>
          </div>
        </div>
      </header>
    </>
  );
}

const styles = `
  * {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
  }

  body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  }

  .header {
    position: sticky;
    top: 0;
    background: white;
    border-bottom: 2px solid rgba(0, 0, 0, 0.05);
    padding: 1rem 2rem;
    z-index: 1000;
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .header.scrolled {
    box-shadow: 0 4px 20px rgba(0, 0, 0, 0.08);
    padding: 0.75rem 2rem;
  }

  /* LOGO */
  .logo {
    font-family: 'Orbitron', 'Arial Black', sans-serif;
    color: #007bff;
    font-weight: 700;
    letter-spacing: 3px;
    text-transform: uppercase;
    font-size: 1.5rem;
    cursor: pointer;
    transition: all 0.3s ease;
    background: linear-gradient(135deg, #007bff 0%, #0056b3 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .logo:hover {
    transform: scale(1.05);
    filter: brightness(1.2);
  }

  /* DESKTOP LAYOUT */
  .header-desktop {
    display: flex;
    align-items: center;
    gap: 2rem;
    max-width: 1400px;
    margin: 0 auto;
  }

  .logo-container {
    flex-shrink: 0;
  }

  /* SEARCH WRAPPER */
  .search-wrapper {
    flex: 1;
    display: flex;
    align-items: center;
    gap: 1rem;
    max-width: 700px;
  }

  /* CATALOG DROPDOWN */
  .catalog-dropdown {
    position: relative;
  }

  .catalog-btn {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    padding: 0.65rem 1.25rem;
    background: #f8f9fa;
    border: 1px solid rgba(0, 0, 0, 0.1);
    border-radius: 12px;
    font-size: 0.95rem;
    font-weight: 500;
    cursor: pointer;
    transition: all 0.3s ease;
    white-space: nowrap;
  }

  .catalog-btn:hover {
    background: #e9ecef;
    border-color: #007bff;
    transform: translateY(-1px);
  }

  .catalog-btn:active {
    transform: translateY(0);
  }

  .chevron {
    transition: transform 0.3s ease;
    color: #007bff;
  }

  .chevron.rotated {
    transform: rotate(180deg);
  }

  .dropdown-menu {
    position: absolute;
    top: calc(100% + 0.5rem);
    left: 0;
    background: white;
    border: 1px solid rgba(0, 0, 0, 0.1);
    border-radius: 12px;
    min-width: 200px;
    padding: 0.5rem;
    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.12);
    opacity: 0;
    visibility: hidden;
    transform: translateY(-10px);
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .dropdown-menu.active {
    opacity: 1;
    visibility: visible;
    transform: translateY(0);
  }

  .dropdown-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    padding: 0.75rem 1rem;
    margin: 0.25rem 0;
    border-radius: 8px;
    font-size: 0.95rem;
    cursor: pointer;
    transition: all 0.2s ease;
    animation: slideInLeft 0.3s ease forwards;
    opacity: 0;
  }

  @keyframes slideInLeft {
    from {
      opacity: 0;
      transform: translateX(-10px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  .dropdown-item:hover {
    background: linear-gradient(135deg, #e7f3ff 0%, #f0f8ff 100%);
    color: #007bff;
    transform: translateX(5px);
  }

  .item-icon {
    font-size: 1.2rem;
  }

  /* SEARCH CONTAINER */
  .search-container {
    position: relative;
    flex: 1;
  }

  .search-input {
    width: 100%;
    padding: 0.75rem 3rem 0.75rem 1.25rem;
    border: 2px solid rgba(0, 0, 0, 0.1);
    border-radius: 25px;
    font-size: 0.95rem;
    outline: none;
    transition: all 0.3s ease;
  }

  .search-input:focus {
    border-color: #007bff;
    box-shadow: 0 0 0 4px rgba(0, 123, 255, 0.1);
    transform: translateY(-1px);
  }

  .search-btn {
    position: absolute;
    right: 8px;
    top: 50%;
    transform: translateY(-50%);
    background: #007bff;
    color: white;
    border: none;
    border-radius: 50%;
    width: 36px;
    height: 36px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: all 0.3s ease;
  }

  .search-btn:hover {
    background: #0056b3;
    transform: translateY(-50%) scale(1.1);
  }

  .search-btn:active {
    transform: translateY(-50%) scale(0.95);
  }

  /* ACTIONS */
  .actions-container {
    display: flex;
    align-items: center;
    gap: 1.5rem;
    flex-shrink: 0;
  }

  .action-btn {
    position: relative;
    background: none;
    border: none;
    color: #007bff;
    cursor: pointer;
    padding: 0.5rem;
    border-radius: 50%;
    transition: all 0.3s ease;
  }

  .action-btn:hover {
    background: #e7f3ff;
    transform: scale(1.1) rotate(5deg);
  }

  .action-btn:active {
    transform: scale(0.95);
  }

  .tooltip {
    position: absolute;
    bottom: -35px;
    left: 50%;
    transform: translateX(-50%);
    background: #1a1a1a;
    color: white;
    padding: 0.4rem 0.8rem;
    border-radius: 6px;
    font-size: 0.8rem;
    white-space: nowrap;
    opacity: 0;
    visibility: hidden;
    transition: all 0.3s ease;
    pointer-events: none;
  }

  .action-btn:hover .tooltip {
    opacity: 1;
    visibility: visible;
    bottom: -40px;
  }

  .cart-btn {
    position: relative;
  }

  .cart-badge {
    position: absolute;
    top: 0;
    right: 0;
    background: #ff4444;
    color: white;
    font-size: 0.7rem;
    font-weight: 600;
    padding: 0.2rem 0.4rem;
    border-radius: 10px;
    min-width: 18px;
    text-align: center;
    animation: pulse 2s infinite;
  }

  @keyframes pulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.1); }
  }

  .sell-now-btn {
    background: linear-gradient(135deg, #007bff 0%, #0056b3 100%);
    color: white;
    border: none;
    padding: 0.75rem 1.5rem;
    border-radius: 25px;
    font-weight: 600;
    font-size: 0.95rem;
    cursor: pointer;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(0, 123, 255, 0.3);
  }

  .sell-now-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 6px 25px rgba(0, 123, 255, 0.4);
  }

  .sell-now-btn:active {
    transform: translateY(0);
  }

  /* MOBILE STYLES */
  .header-mobile {
    display: flex;
    justify-content: space-between;
    align-items: center;
  }

  .menu-toggle {
    background: none;
    border: none;
    color: #007bff;
    cursor: pointer;
    padding: 0.5rem;
    transition: all 0.3s ease;
  }

  .menu-toggle:active {
    transform: scale(0.9);
  }

  .menu-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100vh;
    background: rgba(0, 0, 0, 0.5);
    opacity: 0;
    visibility: hidden;
    transition: all 0.3s ease;
    z-index: 999;
  }

  .menu-overlay.active {
    opacity: 1;
    visibility: visible;
  }

  .mobile-menu {
    position: fixed;
    top: 0;
    right: 0;
    width: 85%;
    max-width: 400px;
    height: 100vh;
    background: white;
    box-shadow: -4px 0 20px rgba(0, 0, 0, 0.15);
    transform: translateX(100%);
    transition: transform 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    z-index: 1000;
    overflow-y: auto;
  }

  .mobile-menu.active {
    transform: translateX(0);
  }

  .mobile-menu-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.5rem;
    border-bottom: 2px solid rgba(0, 0, 0, 0.05);
  }

  .close-btn {
    background: #f8f9fa;
    border: none;
    color: #007bff;
    cursor: pointer;
    padding: 0.5rem;
    border-radius: 50%;
    transition: all 0.3s ease;
  }

  .close-btn:hover {
    background: #e9ecef;
    transform: rotate(90deg);
  }

  .mobile-menu-content {
    padding: 1.5rem;
  }

  .mobile-menu-section {
    margin-bottom: 2rem;
  }

  .mobile-menu-section h4 {
    color: #6c757d;
    font-size: 0.85rem;
    text-transform: uppercase;
    letter-spacing: 1px;
    margin-bottom: 1rem;
  }

  .mobile-menu-section ul {
    list-style: none;
  }

  .mobile-menu-section li {
    display: flex;
    align-items: center;
    gap: 1rem;
    padding: 1rem;
    margin: 0.5rem 0;
    border-radius: 12px;
    cursor: pointer;
    transition: all 0.3s ease;
    font-weight: 500;
  }

  .mobile-menu-section li:hover {
    background: linear-gradient(135deg, #e7f3ff 0%, #f0f8ff 100%);
    color: #007bff;
    transform: translateX(5px);
  }

  .menu-icon {
    font-size: 1.2rem;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .sell-now-btn.mobile {
    width: 100%;
    margin-top: 1rem;
  }

  @media (max-width: 768px) {
    .header {
      padding: 1rem;
    }
  }
`;
